// Helper functions
export function getDeckById(id: string): Deck | undefined {
  return DECKS.find(d => d.id === id);
}

export function getCardsByDeck(deckId: string): PMOCard[] {
  return CARDS.filter(c => c.deckId === deckId);
}

export function getCardById(id: string): PMOCard | undefined {
  return CARDS.find(c => c.id === id);
}

export function searchCards(query: string): PMOCard[] {
  const q = query.toLowerCase();
  return CARDS.filter(c =>
    c.title.toLowerCase().includes(q) ||
    c.code.toLowerCase().includes(q) ||
    c.tagline.toLowerCase().includes(q) ||
    c.whatItIs.toLowerCase().includes(q) ||
    c.tags.some(t => t.toLowerCase().includes(q))
  );
}

export function getRelatedCards(card: PMOCard): PMOCard[] {
  return card.relatedCards
    .map(id => CARDS.find(c => c.code === id || c.id === id))
    .filter(Boolean) as PMOCard[];
}

export const TOTAL_CARDS = CARDS.length;
